// SPDX-License-Identifier: Apache-2.0
pragma solidity >=0.5.0 <0.9.0;
pragma experimental ABIEncoderV2;

import "../system-contracts/hedera-token-service/HederaTokenService.sol";
import "../system-contracts/hedera-token-service/ExpiryHelper.sol";
import "../system-contracts/hedera-token-service/KeyHelper.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

contract RariHedera721DropFactory is HederaTokenService, ExpiryHelper, KeyHelper {

    string name = "tokenName";
    string symbol = "tokenSymbol";
    string memo = "memo";
    int64 initialTotalSupply = 1000;
    int64 maxSupply = 10000;
    int32 decimals = 8;
    bool freezeDefaultStatus = false;

    event ResponseCode(int responseCode);
    event CreatedToken(address tokenAddress);
    event MintedToken(int64 newTotalSupply, int64[] serialNumbers);

    struct FixFeeParams {
        address feeCollector;
        bool isFractionalFee;
        bool isFixedFee;
        int64 feeAmount;
        address fixedFeeTokenAddress;
        bool useHbarsForPayment;
        bool useCurrentTokenForPayment;
        bool isMultipleFixedFee;
        address feeCollector2;
        int64 secondfeeAmount;
        address secondFixedFeeTokenAddress;
        bool useHbarsForPaymentSecondFixFee;
        bool useCurrentTokenForPaymentSecondFixFee;
    }

    struct RoyaltyFeeParams {
        address feeCollector;
        bool isRoyaltyFee;
        int64 feeAmount;
        address fixedFeeTokenAddress;
        bool useHbarsForPayment;
        bool isMultipleRoyaltyFee;
        address feeCollector2;
        int64 secondfeeAmount;
        address secondFixedFeeTokenAddress;
        bool useHbarsForPaymentSecondFixFee;
    }

    function createNonFungibleTokenPublic(
        address treasury
    ) public payable {
        IHederaTokenService.TokenKey[] memory keys = new IHederaTokenService.TokenKey[](2);
        keys[0] = getSingleKey(KeyType.ADMIN, KeyValueType.INHERIT_ACCOUNT_KEY, bytes(""));
        keys[1] = getSingleKey(KeyType.SUPPLY, KeyValueType.INHERIT_ACCOUNT_KEY, bytes(""));


        IHederaTokenService.Expiry memory expiry = IHederaTokenService.Expiry(
            0, treasury, 8000000
        );

        IHederaTokenService.HederaToken memory token = IHederaTokenService.HederaToken(
            name, symbol, treasury, memo, true, maxSupply, freezeDefaultStatus, keys, expiry
        );

        (int responseCode, address tokenAddress) =
        HederaTokenService.createNonFungibleToken(token);

        if (responseCode != HederaResponseCodes.SUCCESS) {
            revert ();
        }

        emit CreatedToken(tokenAddress);
    }

    function createNonFungibleTokenWithCustomFeesPublic(
        address treasury,
        address feeCollector,
        bool isRoyaltyFee,
        bool isFixedFee,
        int64 feeAmount,
        address fixedFeeTokenAddress,
        bool useHbarsForPayment,
        bool useCurrentTokenForPayment
    ) public payable {
        IHederaTokenService.TokenKey[] memory keys = new IHederaTokenService.TokenKey[](2);
        keys[0] = getSingleKey(KeyType.ADMIN, KeyValueType.INHERIT_ACCOUNT_KEY, bytes(""));
        keys[1] = getSingleKey(KeyType.SUPPLY, KeyValueType.INHERIT_ACCOUNT_KEY, bytes(""));


        IHederaTokenService.Expiry memory expiry = IHederaTokenService.Expiry(
            0, treasury, 8000000
        );

        IHederaTokenService.HederaToken memory token = IHederaTokenService.HederaToken(
            name, symbol, treasury, memo, true, maxSupply, freezeDefaultStatus, keys, expiry
        );

        IHederaTokenService.FixedFee[] memory fixedFees = new IHederaTokenService.FixedFee[](1);
        if(isFixedFee) {
            fixedFees[0] = IHederaTokenService.FixedFee(feeAmount, fixedFeeTokenAddress, useHbarsForPayment, useCurrentTokenForPayment, feeCollector);
        } else {
             fixedFees = new IHederaTokenService.FixedFee[](0);
        }

        IHederaTokenService.RoyaltyFee[] memory royaltyFees = new IHederaTokenService.RoyaltyFee[](1);
        if(isRoyaltyFee) {
             royaltyFees[0] = IHederaTokenService.RoyaltyFee(1, 10, feeAmount, fixedFeeTokenAddress, useHbarsForPayment, treasury);
        } else {
             royaltyFees = new IHederaTokenService.RoyaltyFee[](0);
        }

        (int responseCode, address tokenAddress) =
        HederaTokenService.createNonFungibleTokenWithCustomFees(token, fixedFees, royaltyFees);

        if (responseCode != HederaResponseCodes.SUCCESS) {
            revert ();
        }

        emit CreatedToken(tokenAddress);
    }

    function createNonFungibleTokenWithMultipleCustomFeesPublic(
        address treasury,
        FixFeeParams memory params,
        RoyaltyFeeParams memory royaltyParams
    ) public payable {
        IHederaTokenService.TokenKey[] memory keys = new IHederaTokenService.TokenKey[](2);
        keys[0] = getSingleKey(KeyType.ADMIN, KeyValueType.INHERIT_ACCOUNT_KEY, bytes(""));
        keys[1] = getSingleKey(KeyType.SUPPLY, KeyValueType.INHERIT_ACCOUNT_KEY, bytes(""));


        IHederaTokenService.Expiry memory expiry = IHederaTokenService.Expiry(
            0, treasury, 8000000
        );

        IHederaTokenService.HederaToken memory token = IHederaTokenService.HederaToken(
            name, symbol, treasury, memo, true, maxSupply, freezeDefaultStatus, keys, expiry
        );

        IHederaTokenService.FixedFee[] memory fixedFees = new IHederaTokenService.FixedFee[](2);

        if(params.isFixedFee) {
            if(params.isMultipleFixedFee) {
                fixedFees = new IHederaTokenService.FixedFee[](2);
            } else {
                fixedFees = new IHederaTokenService.FixedFee[](1);
            }
        fixedFees[0] = IHederaTokenService.FixedFee(params.feeAmount, params.fixedFeeTokenAddress, params.useHbarsForPayment, params.useCurrentTokenForPayment, params.feeCollector);
            if(params.isMultipleFixedFee) {
        fixedFees[1] = IHederaTokenService.FixedFee(params.secondfeeAmount, params.secondFixedFeeTokenAddress, params.useHbarsForPaymentSecondFixFee, params.useCurrentTokenForPaymentSecondFixFee, params.feeCollector2);
            }
        } else {
            fixedFees = new IHederaTokenService.FixedFee[](0);
        }

        IHederaTokenService.RoyaltyFee[] memory royaltyFees = new IHederaTokenService.RoyaltyFee[](2);

        if(royaltyParams.isRoyaltyFee) {
            if(royaltyParams.isMultipleRoyaltyFee) {
                royaltyFees = new IHederaTokenService.RoyaltyFee[](2);
            } else {
                royaltyFees = new IHederaTokenService.RoyaltyFee[](1);
            }
        royaltyFees[0] = IHederaTokenService.RoyaltyFee(1, 10, royaltyParams.feeAmount, royaltyParams.fixedFeeTokenAddress, royaltyParams.useHbarsForPayment, royaltyParams.feeCollector);
            if(royaltyParams.isMultipleRoyaltyFee) {
        royaltyFees[1] = IHederaTokenService.RoyaltyFee(1, 10, royaltyParams.secondfeeAmount, royaltyParams.secondFixedFeeTokenAddress, royaltyParams.useHbarsForPaymentSecondFixFee, royaltyParams.feeCollector2);
            }
        } else {
            royaltyFees = new IHederaTokenService.RoyaltyFee[](0);
        }

        (int responseCode, address tokenAddress) =
        HederaTokenService.createNonFungibleTokenWithCustomFees(token, fixedFees, royaltyFees);

        if (responseCode != HederaResponseCodes.SUCCESS) {
            revert ();
        }

        emit CreatedToken(tokenAddress);
    }

    function mintTokenPublic(address token, int64 amount, bytes[] memory metadata) public
    returns (int responseCode, int64 newTotalSupply, int64[] memory serialNumbers)  {
        (responseCode, newTotalSupply, serialNumbers) = HederaTokenService.mintToken(token, amount, metadata);
        emit ResponseCode(responseCode);

        if (responseCode != HederaResponseCodes.SUCCESS) {
            revert();
        }

        emit MintedToken(newTotalSupply, serialNumbers);
    }

    function mintTokenToAddressPublic(address token, int64 amount, bytes[] memory metadata) public
    returns (int responseCode, int64 newTotalSupply, int64[] memory serialNumbers)  {
        (responseCode, newTotalSupply, serialNumbers) = HederaTokenService.mintToken(token, amount, metadata);
        emit ResponseCode(responseCode);

        if (responseCode != HederaResponseCodes.SUCCESS) {
            revert();
        }

        emit MintedToken(newTotalSupply, serialNumbers);

        HederaTokenService.transferNFT(token, address(this), msg.sender, serialNumbers[0]);
    }

    function associateTokenPublic(address account, address token) public returns (int responseCode) {
        responseCode = HederaTokenService.associateToken(account, token);
        emit ResponseCode(responseCode);

        if (responseCode != HederaResponseCodes.SUCCESS) {
            revert ();
        }
    }

     function approveNFTPublic(address token, address approved, uint256 serialNumber) public returns (int responseCode) {
        responseCode = HederaTokenService.approveNFT(token, approved, serialNumber);
        emit ResponseCode(responseCode);

        if (responseCode != HederaResponseCodes.SUCCESS) {
            revert ();
        }
    }

    function transferNFTsPublic(address token, address[] memory sender, address[] memory receiver, int64[] memory serialNumber) external returns (int256 responseCode) {
        responseCode = HederaTokenService.transferNFTs(token, sender, receiver, serialNumber);
        emit ResponseCode(responseCode);

        if (responseCode != HederaResponseCodes.SUCCESS) {
            revert ();
        }
    }

    function cryptoTransferPublic(IHederaTokenService.TransferList calldata transferList, IHederaTokenService.TokenTransferList[] calldata tokenTransferList) public returns (int responseCode) {
        responseCode = HederaTokenService.cryptoTransfer(transferList, tokenTransferList);
        emit ResponseCode(responseCode);

        if (responseCode != HederaResponseCodes.SUCCESS) {
            revert();
        }
    }


    function transferFromERC721(address token, address sender, address recipient, uint256 tokenId) external {
        IERC721(token).transferFrom(sender, recipient, tokenId);
    }

    function approveFromERC721(address token, address spender, uint256 tokenId) external {
         IERC721(token).approve(spender, tokenId);
    }
}

contracts/tokens/RariNFTCreator.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.5.0 <0.9.0;

import "../system-contracts/HederaResponseCodes.sol";
import "../system-contracts/hedera-token-service/IHederaTokenService.sol";
import "../system-contracts/hedera-token-service/HederaTokenService.sol";
import "../system-contracts/hedera-token-service/ExpiryHelper.sol";
import "../system-contracts/hedera-token-service/KeyHelper.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

contract RariNFTCreator is ExpiryHelper, KeyHelper, HederaTokenService {

    struct CreateNftCollectionParams {
        address feeCollector;
        bool isRoyaltyFee;
        bool isFixedFee;
        int64 feeAmount;
        address fixedFeeTokenAddress;
        bool useHbarsForPayment;
        bool useCurrentTokenForPayment;
    }

    event CreatedToken(address tokenAddress);
    event MintTokenStatus(int256 status);

    mapping(address => string) public baseUri;

    function createNftCollection(
        string memory name,
        string memory symbol,
        string memory memo,
        int64 maxSupply,
        int64 autoRenewPeriod
    ) external payable returns (address) {
        IHederaTokenService.TokenKey[] memory keys = new IHederaTokenService.TokenKey[](1);
        // Set this contract as supply for the token
        keys[0] = getSingleKey(KeyType.SUPPLY, KeyValueType.CONTRACT_ID, address(this));

        IHederaTokenService.HederaToken memory token;
        token.name = name;
        token.symbol = symbol;
        token.memo = memo;
        token.treasury = address(this);
        token.tokenSupplyType = true; // set supply to FINITE
        token.maxSupply = maxSupply;
        token.tokenKeys = keys;
        token.freezeDefault = false;
        token.expiry = createAutoRenewExpiry(address(this), autoRenewPeriod); // Contract auto-renews the token

        (int responseCode, address createdToken) =
            HederaTokenService.createNonFungibleToken(token);

        if (responseCode != HederaResponseCodes.SUCCESS) {
            revert("Failed to create non-fungible token");
        }

        emit CreatedToken(createdToken);

        return createdToken;
    }

    function createNonFungibleTokenWithCustomFeesPublic(
        string memory name,
        string memory symbol,
        string memory memo,
        int64 maxSupply,
        string memory uri,
        CreateNftCollectionParams memory params
    ) public payable {
        IHederaTokenService.TokenKey[] memory keys = new IHederaTokenService.TokenKey[](2);
        keys[0] = getSingleKey(KeyType.ADMIN, KeyValueType.INHERIT_ACCOUNT_KEY, bytes(""));
        keys[1] = getSingleKey(KeyType.SUPPLY, KeyValueType.INHERIT_ACCOUNT_KEY, bytes(""));

        IHederaTokenService.Expiry memory expiry = IHederaTokenService.Expiry(0, address(this), 8000000);

        IHederaTokenService.HederaToken memory token = IHederaTokenService.HederaToken(
            name,
            symbol,
            address(this),
            memo,
            true,
            maxSupply,
            false,
            keys,
            expiry
        );

        IHederaTokenService.FixedFee[] memory fixedFees = new IHederaTokenService.FixedFee[](1);
        if (params.isFixedFee) {
            fixedFees[0] = IHederaTokenService.FixedFee(
                params.feeAmount,
                params.fixedFeeTokenAddress,
                params.useHbarsForPayment,
                params.useCurrentTokenForPayment,
                params.feeCollector
            );
        } else {
            fixedFees = new IHederaTokenService.FixedFee[](0);
        }

        IHederaTokenService.RoyaltyFee[] memory royaltyFees = new IHederaTokenService.RoyaltyFee[](1);
        // up to 10
        // fee can't be feeAmount equal to zero
        if (params.isRoyaltyFee) {
            royaltyFees[0] = IHederaTokenService.RoyaltyFee(
                1,
                10,
                params.feeAmount,
                params.fixedFeeTokenAddress,
                params.useHbarsForPayment,
                params.feeCollector
            );
        } else {
            royaltyFees = new IHederaTokenService.RoyaltyFee[](0);
        }

        (int responseCode, address tokenAddress) =
            HederaTokenService.createNonFungibleTokenWithCustomFees(token, fixedFees, royaltyFees);

        if (responseCode != HederaResponseCodes.SUCCESS) {
            revert();
        }

        baseUri[tokenAddress] = uri;

        emit CreatedToken(tokenAddress);
    }

    function convertStringToBytes(string memory input) public pure returns (bytes memory) {
        return bytes(input);
    }

    // token id starts at 1
    function mintNft(address token) external payable returns (int64) {
        string memory uri = baseUri[token];
        (int responseCode, IHederaTokenService.TokenInfo memory tokenInfo) =
            HederaTokenService.getTokenInfo(token);

        if (responseCode != HederaResponseCodes.SUCCESS) {
            revert(string.concat("Failed to get infor for collection non-fungible token; status: ", intToString(int256(responseCode))));
        }
        int64 nextTokenId = tokenInfo.totalSupply + 1;
        string memory tokenUri = string.concat(uri, "/", int64ToString(nextTokenId), ".json");
        bytes[] memory metadata = new bytes[](1);
        metadata[0] = bytes(tokenUri);

        (int response, , int64[] memory serial) = HederaTokenService.mintToken(token, 0, metadata);
        if (response != HederaResponseCodes.SUCCESS) {
            revert(string.concat("Failed to mint non-fungible token; status: ", intToString(int256(response))));
        }

        int responseCodeTransfer = HederaTokenService.transferNFT(token, address(this), msg.sender, serial[0]);
        if (responseCodeTransfer != HederaResponseCodes.SUCCESS) {
            revert(string.concat("Failed to transfer non-fungible token; status: ", intToString(int256(responseCodeTransfer)), " tokenId: ", intToString(int256(serial[0]))));
        }

        return serial[0];
    }

        // token id starts at 1
    function mintNftTo(address token, address to) external payable returns (int64) {
        string memory uri = baseUri[token];
        (int responseCode, IHederaTokenService.TokenInfo memory tokenInfo) =
            HederaTokenService.getTokenInfo(token);

        if (responseCode != HederaResponseCodes.SUCCESS) {
            revert(string.concat("Failed to get infor for collection non-fungible token; status: ", intToString(int256(responseCode))));
        }
        int64 nextTokenId = tokenInfo.totalSupply + 1;
        string memory tokenUri = string.concat(uri, "/", int64ToString(nextTokenId), ".json");
        bytes[] memory metadata = new bytes[](1);
        metadata[0] = bytes(tokenUri);

        (int response, , int64[] memory serial) = HederaTokenService.mintToken(token, 0, metadata);
        if (response != HederaResponseCodes.SUCCESS) {
            revert(string.concat("Failed to mint non-fungible token; status: ", intToString(int256(response))));
        }

        int responseCodeTransfer = HederaTokenService.transferNFT(token, address(this), to, serial[0]);
        if (responseCodeTransfer != HederaResponseCodes.SUCCESS) {
            revert(string.concat("Failed to transfer non-fungible token; status: ", intToString(int256(responseCodeTransfer))));
        }

        return serial[0];
    }


    /**
     * @dev Example function that logs both the input metadata and a
     *      newly-generated metadata to compare the two.
     */
    function mintNftWithMetadata(address token, bytes[] memory metadata) external returns (int64) {

        (int response, , int64[] memory serial) = HederaTokenService.mintToken(token, 0, metadata);

        // Create a 'generated' metadata for comparison
        string memory uri = baseUri[token];
        (int responseCode, IHederaTokenService.TokenInfo memory tokenInfo) =
            HederaTokenService.getTokenInfo(token);

        if (responseCode != HederaResponseCodes.SUCCESS) {
            revert("Failed to mint non-fungible token");
        }
        int64 totalSupply = tokenInfo.totalSupply;
        string memory tokenUri = string.concat(uri, "/", int64ToString(totalSupply), ".json");

        // Suppose you generate a new metadata array with one entry
        bytes[] memory metadatagen = new bytes[](1);
        metadatagen[0] = bytes(tokenUri);

        if (response != HederaResponseCodes.SUCCESS) {
            revert("Failed to mint non-fungible token");
        }

        int responseCodeTransfer = HederaTokenService.transferNFT(token, address(this), msg.sender, serial[0]);
        if (responseCodeTransfer != HederaResponseCodes.SUCCESS) {
            revert("Failed to transfer non-fungible token");
        }

        return serial[0];
    }

    function transferNft(address token, address receiver, int64 serial) external returns (int) {
        int response = HederaTokenService.transferNFT(token, address(this), receiver, serial);
        if (response != HederaResponseCodes.SUCCESS) {
            revert("Failed to transfer non-fungible token");
        }
        return response;
    }

    function int64ToString(int64 value) public pure returns (string memory) {
        // Handle zero explicitly
        if (value == 0) {
            return "0";
        }

        bool negative = (value < 0);
        // Convert to positive if negative
        uint64 temp = negative ? uint64(-value) : uint64(value);

        // Determine the length
        uint256 length;
        uint64 tempCopy = temp;
        while (tempCopy != 0) {
            length++;
            tempCopy /= 10;
        }

        // Account for '-'
        if (negative) {
            length++;
        }

        bytes memory buffer = new bytes(length);
        uint256 index = length - 1;

        while (temp != 0) {
            buffer[index] = bytes1(uint8(48 + (temp % 10)));
            temp /= 10;
            if (index > 0) {
                index--;
            }
        }

        // If negative
        if (negative) {
            buffer[0] = '-';
        }

        return string(buffer);
    }

    function intToString(int256 value) internal pure returns (string memory) {
        // Special case: zero
        if (value == 0) {
            return "0";
        }

        bool negative = value < 0;
        // Take absolute value (unchecked to allow minimum int256)
        uint256 tempValue = negative ? uint256(-value) : uint256(value);

        // Determine length of the string
        uint256 digits;
        uint256 temp = tempValue;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }

        // If negative, we need extra space for '-'
        if (negative) {
            digits++;
        }

        // Create a buffer for the characters
        bytes memory buffer = new bytes(digits);
        uint256 index = digits - 1;

        // Write digits into buffer (in reverse order)
        while (tempValue != 0) {
            buffer[index] = bytes1(uint8(48 + tempValue % 10)); // '0' = 48
            tempValue /= 10;
            if (index > 0) {
                index--;
            }
        }

        // If negative, set the '-' sign
        if (negative) {
            buffer[0] = "-";
        }

        return string(buffer);
    }
}
