    }

    function mint(address account, uint256 amount) public onlyOwner returns (bool) {
        require(!mintStopped, "mint is stopped");
        _mint(account, amount);
        return true;
    }

    function stopMint() public onlyOwner {
        mintStopped = true;
    }
