// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.13;

import "./interfaces/ExchangeProtocolFee.sol";

contract SetProtocolFeeAction {
    ExchangeProtocolFee public immutable exchangeV2;

    constructor(ExchangeProtocolFee _exchangeV2) {
        exchangeV2 = _exchangeV2;
    }

    function perform(address _receiver, uint48 _buyerAmount, uint48 _sellerAmount) external {
        exchangeV2.setAllProtocolFeeData(_receiver, _buyerAmount, _sellerAmount);
    }
}

src/actions/TimelockAdminshipTransferAnd.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.13;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";

contract TimelockAdminshipTransferAndRenounceAction {
    bytes32 public constant TIMELOCK_ADMIN_ROLE = keccak256("TIMELOCK_ADMIN_ROLE");

    function perform(address target, address newOwner) public {
        AccessControlUpgradeable(target).grantRole(TIMELOCK_ADMIN_ROLE, newOwner);
        AccessControlUpgradeable(target).renounceRole(TIMELOCK_ADMIN_ROLE, address(this));
    }
}

src/UpgradeExecutor.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.16;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Address.sol";

/// @title  A root contract from which it execute upgrades
/// @notice Does not contain upgrade logic itself, only the means to call upgrade contracts and execute them
/// @dev    We use these upgrade contracts as they allow multiple actions to take place in an upgrade
///         and for these actions to interact. However because we are delegatecalling into these upgrade
///         contracts, it's important that these upgrade contract do not touch or modify contract state.
contract UpgradeExecutor is AccessControl, ReentrancyGuard {
    using Address for address;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant EXECUTOR_ROLE = keccak256("EXECUTOR_ROLE");

    /// @notice Emitted when an upgrade execution occurs
    event UpgradeExecuted(address indexed upgrade, uint256 value, bytes data);

    /// @notice Initialise the upgrade executor
    /// @param executors Can call the execute function - EXECUTOR_ROLE
    constructor(address[] memory executors) ReentrancyGuard() {
        address admin = address(this);

        _setRoleAdmin(ADMIN_ROLE, ADMIN_ROLE);
        _setRoleAdmin(EXECUTOR_ROLE, ADMIN_ROLE);

        _grantRole(ADMIN_ROLE, admin);
        for (uint256 i = 0; i < executors.length; ++i) {
            _grantRole(EXECUTOR_ROLE, executors[i]);
        }
    }

    /// @notice Execute an upgrade by delegate calling an upgrade contract
    /// @dev    Only executor can call this. Since we're using a delegatecall here the Upgrade contract
    ///         will have access to the state of this contract - including the roles. Only upgrade contracts
    ///         that do not touch local state should be used.
    function execute(address upgrade, bytes memory upgradeCallData)
        public
        payable
        onlyRole(EXECUTOR_ROLE)
        nonReentrant
    {
        // OZ Address library check if the address is a contract and bubble up inner revert reason
        address(upgrade).functionDelegateCall(
            upgradeCallData, "UpgradeExecutor: inner delegate call failed without reason"
        );

        emit UpgradeExecuted(upgrade, msg.value, upgradeCallData);
    }
}

test/contracts/TestHelper.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "../../src/actions/ProxyUpgradeAction.sol";
import "../../src/actions/SetProtocolFeeAction.sol";
import "../../src/actions/OwnershipTransferAction.sol";
import "../../src/actions/TimelockAdminshipTransferAndRenounceAction.sol";

import "../../src/UpgradeExecutor.sol";

contract TestHelper {

    function encodeProxyUpgradeCall(address admin, address payable target, address newLogic) external pure returns(bytes memory) {
        return abi.encodeWithSelector(ProxyUpgradeAction.perform.selector, admin, target, newLogic);
    }

    function encodeProtocolFeeCall(address _receiver, uint48 _buyerAmount, uint48 _sellerAmount) external pure returns(bytes memory) {
        return abi.encodeWithSelector(SetProtocolFeeAction.perform.selector, _receiver, _buyerAmount, _sellerAmount);
    }

    function encodeOwnershipTransferCall(address target, address newOwner) external pure returns(bytes memory) {
        return abi.encodeWithSelector(OwnershipTransferAction.perform.selector, target, newOwner);
    }

    function encodeAdminshipTimelockCall(address target, address newOwner) external pure returns(bytes memory) {
        return abi.encodeWithSelector(TimelockAdminshipTransferAndRenounceAction.perform.selector, target, newOwner);
    }

    function encodeUpgradeActionCall(address upgrade, bytes memory data) external pure returns(bytes memory) {
        return abi.encodeWithSelector(UpgradeExecutor.execute.selector, upgrade, data);
    }

    function hashDescription(string calldata description) external pure returns(bytes32) {
        return keccak256(bytes(description));
    }

}
