pragma solidity ^0.8.30;
import "../erc-1155/ERC1155Rarible.sol";
import "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
/**
 * @dev This contract is for creating proxy to access ERC1155Rarible token.
 *
 * The beacon should be initialized before call ERC1155RaribleFactoryC2 constructor.
 *
 */
contract ERC1155RaribleFactoryC2 is Ownable {
    address public beacon;
    address transferProxy;
    address lazyTransferProxy;
    event Create1155RaribleProxy(address proxy);
    event Create1155RaribleUserProxy(address proxy);
    constructor(address _beacon, address _transferProxy, address _lazyTransferProxy) Ownable(msg.sender) {
        beacon = _beacon;
        transferProxy = _transferProxy;
        lazyTransferProxy = _lazyTransferProxy;
    }
    function createToken(
        string memory _name,
        string memory _symbol,
        string memory baseURI,
        string memory contractURI,
        address initialOwner,
        uint salt
    ) external {
        address beaconProxy = deployProxy(getData(_name, _symbol, baseURI, contractURI, initialOwner), salt);
        ERC1155Rarible token = ERC1155Rarible(beaconProxy);
        emit Create1155RaribleProxy(beaconProxy);
    }
    function createToken(
        string memory _name,
        string memory _symbol,
        string memory baseURI,
        string memory contractURI,
        address[] memory operators,
        address initialOwner,
        uint salt
    ) external {
        address beaconProxy = deployProxy(getData(_name, _symbol, baseURI, contractURI, operators, initialOwner), salt);
        ERC1155Rarible token = ERC1155Rarible(address(beaconProxy));
        emit Create1155RaribleUserProxy(beaconProxy);
    }
    //deploying BeaconProxy contract with create2
    function deployProxy(bytes memory data, uint salt) internal returns (address proxy) {
        bytes memory bytecode = getCreationBytecode(data);
        assembly {
            proxy := create2(0, add(bytecode, 0x20), mload(bytecode), salt)
            if iszero(extcodesize(proxy)) {
                revert(0, 0)
            }
        }
    }
    //adding constructor arguments to BeaconProxy bytecode
    function getCreationBytecode(bytes memory _data) internal view returns (bytes memory) {
        return abi.encodePacked(type(BeaconProxy).creationCode, abi.encode(beacon, _data));
    }
    //returns address that contract with such arguments will be deployed on
    function getAddress(
        string memory _name,
        string memory _symbol,
        string memory baseURI,
        string memory contractURI,
        address initialOwner,
        uint _salt
    ) public view returns (address) {
        bytes memory bytecode = getCreationBytecode(getData(_name, _symbol, baseURI, contractURI, initialOwner));
        bytes32 hash = keccak256(abi.encodePacked(bytes1(0xff), address(this), _salt, keccak256(bytecode)));
        return address(uint160(uint(hash)));
    }
    function getData(
        string memory _name,
        string memory _symbol,
        string memory baseURI,
        string memory contractURI,
        address initialOwner
    ) internal view returns (bytes memory) {
        return
            abi.encodeWithSelector(
                ERC1155Rarible.__ERC1155Rarible_init.selector,
                _name,
                _symbol,
                baseURI,
                contractURI,
                transferProxy,
                lazyTransferProxy,
                initialOwner
            );
    }
    //returns address that contract with such arguments will be deployed on
    function getAddress(
        string memory _name,
        string memory _symbol,
        string memory baseURI,
        string memory contractURI,
        address[] memory operators,
        address initialOwner,
        uint _salt
    ) public view returns (address) {
        bytes memory bytecode = getCreationBytecode(
            getData(_name, _symbol, baseURI, contractURI, operators, initialOwner)
        );
        bytes32 hash = keccak256(abi.encodePacked(bytes1(0xff), address(this), _salt, keccak256(bytecode)));
        return address(uint160(uint(hash)));
    }
    function getData(
        string memory _name,
        string memory _symbol,
        string memory baseURI,
        string memory contractURI,
        address[] memory operators,
        address initialOwner
    ) internal view returns (bytes memory) {
        return
            abi.encodeWithSelector(
                ERC1155Rarible.__ERC1155RaribleUser_init.selector,
                _name,
                _symbol,
                baseURI,
                contractURI,
                operators,
                transferProxy,
                lazyTransferProxy,
                initialOwner
            );
    }
}
