// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {ERC721HolderUpgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC721/utils/ERC721HolderUpgradeable.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {AccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import {IERC721Receiver} from "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
/// @title NftPool
/// @notice Single pool holding all NFTs, organized by pool levels based on price ranges.
/// @dev Collections are assigned to a pool level by floor price.
/// NFTs are tracked per collection; pools maintain aggregate counts (no per-NFT migration on floor change).
contract NftPool is Initializable, ERC721HolderUpgradeable, OwnableUpgradeable, AccessControlUpgradeable {
    // -----------------------
    // Types
    // -----------------------
    /// @dev Pool levels ordered from common to rare
    enum PoolLevel {
        Common,
        Rare,
        Epic,
        Legendary,
        UltraRare
    }
    /// @dev Number of defined pool levels (update if enum is extended)
    uint256 private constant _POOL_LEVEL_COUNT = uint256(PoolLevel.UltraRare) + 1;
    /// @dev Pool price range configuration + collections + aggregate NFT count
    struct PoolInfo {
        uint256 lowPrice; // Minimum floor price (inclusive)
        uint256 highPrice; // Maximum floor price (exclusive)
        address[] collections; // Collections currently assigned to this pool
        uint256 totalNfts; // Total NFTs (across all collections) in this pool
    }
    /// @dev Lightweight config struct for pool ranges (no collections/total)
    struct PoolRange {
        uint256 lowPrice;
        uint256 highPrice;
    }
    /// @dev Per-collection configuration and pool membership
    /// @notice "Allowed" is derived from poolIndexPlusOne != 0
    struct CollectionInfo {
        uint256 floorPrice; // Current floor price in wei
        PoolLevel poolLevel; // Logical pool level for this collection
        uint256 poolIndexPlusOne; // 1-based index in PoolInfo.collections, 0 = not assigned / not allowed
    }
    // -----------------------
    // Storage
    // -----------------------
    /// @dev Pool level => price range configuration + collections + aggregate count
    mapping(PoolLevel => PoolInfo) private _poolInfo;
    /// @dev Collection address => collection info
    mapping(address => CollectionInfo) private _collectionInfo;
    /// @dev Per-collection NFTs held by the pool (only tokenIds, collection is the key)
    mapping(address => uint256[]) private _collectionTokens;
    /// @dev Index of a tokenId inside _collectionTokens[collection] (1-based, 0 = not tracked)
    mapping(address => mapping(uint256 => uint256)) private _collectionTokenIndexPlusOne;
    // -----------------------
    // Roles
    // -----------------------
    bytes32 public constant POOL_MANAGER_ROLE = keccak256("POOL_MANAGER_ROLE");
    // -----------------------
    // Events
    // -----------------------
    event Deposited(address indexed collection, uint256 indexed tokenId, PoolLevel indexed poolLevel);
    event Withdrawn(address indexed to, address indexed collection, uint256 indexed tokenId, PoolLevel poolLevel);
    event PoolInfoUpdated(PoolLevel indexed poolLevel, uint256 lowPrice, uint256 highPrice);
    event CollectionConfigured(address indexed collection, bool allowed, uint256 floorPrice);
    event CollectionFloorPriceUpdated(address indexed collection, uint256 oldPrice, uint256 newPrice);
    event CollectionAllowedUpdated(address indexed collection, bool allowed);
    event RescuedNft(address indexed to, address indexed collection, uint256 indexed tokenId);
    // -----------------------
    // Errors
    // -----------------------
    error CollectionNotAllowed();
    error NotInPool();
    error ZeroAddress();
    error ArrayLengthMismatch();
    error LevelEmpty(PoolLevel level);
    error FloorPriceNotSet();
    error InvalidPriceRange();
    error IndexOutOfBounds();
    error AlreadyTracked();
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }
    // -----------------------
    // Init
    // -----------------------
    /// @notice Initialize pool with owner and optional custom pool ranges
    /// @param initialOwner Owner / admin address
    /// @param ranges Optional array of pool ranges, length must equal number of pool levels.
    /// If empty, default ETH ranges are used.
    function initialize(address initialOwner, PoolRange[] calldata ranges) external initializer {
        if (initialOwner == address(0)) revert ZeroAddress();
        __ERC721Holder_init();
        __Ownable_init(initialOwner);
        __AccessControl_init();
        _grantRole(DEFAULT_ADMIN_ROLE, initialOwner);
        _grantRole(POOL_MANAGER_ROLE, initialOwner);
        if (ranges.length == 0) {
            // Default price ranges (in wei), scaled for 10% average profit
            // Common: 0 - 0.05325 ETH
            // Rare: 0.05325 - 0.213 ETH
            // Epic: 0.213 - 1.065 ETH
            // Legendary: 1.065 - 5.325 ETH
            // UltraRare: 5.325+ ETH (no practical upper bound)
            _poolInfo[PoolLevel.Common].lowPrice = 0;
            _poolInfo[PoolLevel.Common].highPrice = 0.05325 ether;
            _poolInfo[PoolLevel.Rare].lowPrice = 0.05325 ether;
            _poolInfo[PoolLevel.Rare].highPrice = 0.213 ether;
            _poolInfo[PoolLevel.Epic].lowPrice = 0.213 ether;
            _poolInfo[PoolLevel.Epic].highPrice = 1.065 ether;
            _poolInfo[PoolLevel.Legendary].lowPrice = 1.065 ether;
            _poolInfo[PoolLevel.Legendary].highPrice = 5.325 ether;
            _poolInfo[PoolLevel.UltraRare].lowPrice = 5.325 ether;
            _poolInfo[PoolLevel.UltraRare].highPrice = type(uint256).max;
        } else {
            if (ranges.length != _POOL_LEVEL_COUNT) revert ArrayLengthMismatch();
            for (uint256 i = 0; i < ranges.length; i++) {
                PoolRange calldata cfg = ranges[i];
                if (cfg.lowPrice >= cfg.highPrice) revert InvalidPriceRange();
                PoolLevel level = PoolLevel(i);
                PoolInfo storage p = _poolInfo[level];
                p.lowPrice = cfg.lowPrice;
                p.highPrice = cfg.highPrice;
                emit PoolInfoUpdated(level, cfg.lowPrice, cfg.highPrice);
            }
        }
    }
    // -----------------------
    // Admin: Pool Configuration
    // -----------------------
    /// @notice Set price range for a pool level
    /// @param level The pool level to configure
    /// @param lowPrice Minimum floor price (inclusive)
    /// @param highPrice Maximum floor price (exclusive)
    function setPoolInfo(PoolLevel level, uint256 lowPrice, uint256 highPrice) external onlyOwner {
        if (lowPrice >= highPrice) revert InvalidPriceRange();
        PoolInfo storage info = _poolInfo[level];
        info.lowPrice = lowPrice;
        info.highPrice = highPrice;
        emit PoolInfoUpdated(level, lowPrice, highPrice);
    }
    /// @notice Batch set all pool price ranges via array
    /// @dev ranges.length must equal number of pool levels
    function setAllPoolInfo(PoolRange[] calldata ranges) external onlyOwner {
        if (ranges.length != _POOL_LEVEL_COUNT) revert ArrayLengthMismatch();
        for (uint256 i = 0; i < ranges.length; i++) {
            PoolRange calldata cfg = ranges[i];
            if (cfg.lowPrice >= cfg.highPrice) revert InvalidPriceRange();
            PoolLevel level = PoolLevel(i);
            PoolInfo storage info = _poolInfo[level];
            info.lowPrice = cfg.lowPrice;
            info.highPrice = cfg.highPrice;
            emit PoolInfoUpdated(level, cfg.lowPrice, cfg.highPrice);
        }
    }
    // -----------------------
    // Internal: Collection Pool Membership
    // -----------------------
    function _addCollectionToPool(address collection, PoolLevel level) internal {
        PoolInfo storage pool = _poolInfo[level];
        pool.collections.push(collection);
        uint256 indexPlusOne = pool.collections.length;
        CollectionInfo storage info = _collectionInfo[collection];
        info.poolIndexPlusOne = indexPlusOne;
        info.poolLevel = level;
    }
    function _removeCollectionFromPool(address collection, PoolLevel level) internal {
        CollectionInfo storage info = _collectionInfo[collection];
        uint256 indexPlusOne = info.poolIndexPlusOne;
        if (indexPlusOne == 0) {
            return; // already not assigned
        }
        PoolInfo storage pool = _poolInfo[level];
        uint256 index = indexPlusOne - 1;
        uint256 lastIndex = pool.collections.length - 1;
        if (index != lastIndex) {
            address lastCollection = pool.collections[lastIndex];
            pool.collections[index] = lastCollection;
            CollectionInfo storage lastInfo = _collectionInfo[lastCollection];
            lastInfo.poolIndexPlusOne = index + 1;
            // lastInfo.poolLevel stays 'level'
        }
        pool.collections.pop();
        info.poolIndexPlusOne = 0;
        // info.poolLevel will be updated by caller (_setCollectionConfig)
    }
    /// @dev Core function to configure collection, used by all public setters
    function _setCollectionConfig(address collection, bool allowed, uint256 newFloorPrice) internal {
        if (allowed && newFloorPrice == 0) revert FloorPriceNotSet();
        CollectionInfo storage info = _collectionInfo[collection];
        bool wasAllowed = info.poolIndexPlusOne != 0;
        uint256 oldPrice = info.floorPrice;
        PoolLevel oldLevel = info.poolLevel;
        uint256 tokensCount = _collectionTokens[collection].length;
        info.floorPrice = newFloorPrice;
        PoolLevel newLevel = _getPoolLevelForPrice(newFloorPrice);
        // membership transitions
        if (wasAllowed) {
            if (!allowed) {
                // allowed -> not allowed
                _removeCollectionFromPool(collection, oldLevel);
                if (tokensCount > 0) {
                    _poolInfo[oldLevel].totalNfts -= tokensCount;
                }
            } else {
                // allowed -> allowed
                if (newLevel != oldLevel) {
                    _removeCollectionFromPool(collection, oldLevel);
                    _addCollectionToPool(collection, newLevel);
                    if (tokensCount > 0) {
                        _poolInfo[oldLevel].totalNfts -= tokensCount;
                        _poolInfo[newLevel].totalNfts += tokensCount;
                    }
                }
            }
        } else {
            if (allowed) {
                // not allowed -> allowed
                _addCollectionToPool(collection, newLevel);
                if (tokensCount > 0) {
                    _poolInfo[newLevel].totalNfts += tokensCount;
                }
            }
            // not allowed -> not allowed : no pool membership
        }
        // logical level always updated
        info.poolLevel = newLevel;
        emit CollectionConfigured(collection, allowed, newFloorPrice);
        if (oldPrice != newFloorPrice) {
            emit CollectionFloorPriceUpdated(collection, oldPrice, newFloorPrice);
        }
        if (wasAllowed != allowed) {
            emit CollectionAllowedUpdated(collection, allowed);
        }
    }
    // -----------------------
    // Admin: Collection Configuration
    // -----------------------
    /// @notice Configure a collection (allowed status and floor price)
    /// @param collection NFT collection address
    /// @param allowed Whether deposits are accepted (true => collection is included in a pool)
    /// @param floorPrice Current floor price in wei
    function configureCollection(address collection, bool allowed, uint256 floorPrice) external onlyRole(POOL_MANAGER_ROLE) {
        if (collection == address(0)) revert ZeroAddress();
        _setCollectionConfig(collection, allowed, floorPrice);
    }
    /// @notice Batch configure multiple collections
    function configureCollections(
        address[] calldata collections,
        bool[] calldata allowedList,
        uint256[] calldata floorPrices
    ) external onlyRole(POOL_MANAGER_ROLE) {
        if (collections.length != allowedList.length || collections.length != floorPrices.length) {
            revert ArrayLengthMismatch();
        }
        for (uint256 i = 0; i < collections.length; i++) {
            if (collections[i] == address(0)) revert ZeroAddress();
            _setCollectionConfig(collections[i], allowedList[i], floorPrices[i]);
        }
    }
    /// @notice Update floor price for a collection and mark it as allowed
    /// @dev Pool level and aggregate NFT counts are adjusted without per-NFT migration.
    /// Calling this will always mark the collection as allowed.
    function setCollectionFloorPrice(address collection, uint256 newPrice) external onlyRole(POOL_MANAGER_ROLE) {
        if (collection == address(0)) revert ZeroAddress();
        _setCollectionConfig(collection, true, newPrice);
    }
    /// @notice Batch update floor prices and mark all collections as allowed
    function setCollectionFloorPrices(
        address[] calldata collections,
        uint256[] calldata floorPrices
    ) external onlyRole(POOL_MANAGER_ROLE) {
        if (collections.length != floorPrices.length) revert ArrayLengthMismatch();
        for (uint256 i = 0; i < collections.length; i++) {
            if (collections[i] == address(0)) revert ZeroAddress();
            _setCollectionConfig(collections[i], true, floorPrices[i]);
        }
    }
    // -----------------------
    // Internal: NFT Bookkeeping
    // -----------------------
    function _addCollectionToken(address collection, uint256 tokenId, PoolLevel level) internal {
        // protect from duplicates
        if (_collectionTokenIndexPlusOne[collection][tokenId] != 0) revert AlreadyTracked();
        uint256[] storage tokens = _collectionTokens[collection];
        tokens.push(tokenId);
        _collectionTokenIndexPlusOne[collection][tokenId] = tokens.length;
        // Only count NFTs of allowed collections in pool totals
        if (_collectionInfo[collection].poolIndexPlusOne != 0) {
            _poolInfo[level].totalNfts += 1;
        }
        emit Deposited(collection, tokenId, level);
    }
    function _removeCollectionToken(address collection, uint256 tokenId, PoolLevel level) internal {
        uint256 indexPlusOne = _collectionTokenIndexPlusOne[collection][tokenId];
        if (indexPlusOne == 0) revert NotInPool();
        uint256[] storage tokens = _collectionTokens[collection];
        uint256 index = indexPlusOne - 1;
        uint256 lastIndex = tokens.length - 1;
        if (index != lastIndex) {
            uint256 lastTokenId = tokens[lastIndex];
            tokens[index] = lastTokenId;
            _collectionTokenIndexPlusOne[collection][lastTokenId] = index + 1;
        }
        tokens.pop();
        delete _collectionTokenIndexPlusOne[collection][tokenId];
        // Adjust totalNfts only if collection is currently allowed
        if (_collectionInfo[collection].poolIndexPlusOne != 0) {
            _poolInfo[level].totalNfts -= 1;
        }
    }
    // -----------------------
    // Pool Operations
    // -----------------------
    /// @notice Deposit an NFT into the pool
    /// @dev Uses safeTransferFrom; we also add to internal bookkeeping here
    /// in case some collections don't properly call onERC721Received.
    function deposit(address collection, uint256 tokenId) external {
        CollectionInfo storage info = _collectionInfo[collection];
        if (info.poolIndexPlusOne == 0) revert CollectionNotAllowed();
        if (info.floorPrice == 0) revert FloorPriceNotSet();
        IERC721(collection).safeTransferFrom(msg.sender, address(this), tokenId);
        // If for some reason onERC721Received was not called or didn't track,
        // ensure the NFT is accounted here.
        if (_collectionTokenIndexPlusOne[collection][tokenId] == 0) {
            _addCollectionToken(collection, tokenId, info.poolLevel);
        }
    }
    /// @notice Transfer an NFT out of the pool
    function transferNft(address collection, address to, uint256 tokenId) external onlyRole(POOL_MANAGER_ROLE) {
        CollectionInfo storage info = _collectionInfo[collection];
        PoolLevel level = info.poolLevel;
        _removeCollectionToken(collection, tokenId, level);
        IERC721(collection).safeTransferFrom(address(this), to, tokenId);
        emit Withdrawn(to, collection, tokenId, level);
    }
    /// @notice Rescue an NFT that was sent incorrectly and is not tracked
    function rescueNft(address collection, address to, uint256 tokenId) external onlyOwner {
        require(_collectionTokenIndexPlusOne[collection][tokenId] == 0, "NftPool: NFT is tracked");
        require(IERC721(collection).ownerOf(tokenId) == address(this), "NftPool: not owned");
        IERC721(collection).safeTransferFrom(address(this), to, tokenId);
        emit RescuedNft(to, collection, tokenId);
    }
    // -----------------------
    // ERC721 Receiver
    // -----------------------
    function onERC721Received(
        address /* operator */,
        address /* from */,
        uint256 tokenId,
        bytes memory /* data */
    ) public virtual override returns (bytes4) {
        address collection = msg.sender;
        CollectionInfo storage info = _collectionInfo[collection];
        // Only track if collection is configured (in some pool) and price > 0
        if (info.poolIndexPlusOne != 0 && info.floorPrice > 0) {
            _addCollectionToken(collection, tokenId, info.poolLevel);
        }
        return this.onERC721Received.selector;
    }
    // -----------------------
    // Internal: Pool Level Logic
    // -----------------------
    /// @dev Determine pool level based on floor price using configured ranges
    /// @notice Iterates over all levels; if overlapping ranges exist,
    /// the highest index level that matches wins.
    function _getPoolLevelForPrice(uint256 price) internal view returns (PoolLevel) {
        // walk from highest index to lowest
        for (uint256 i = _POOL_LEVEL_COUNT; i > 0; i--) {
            PoolLevel level = PoolLevel(i - 1);
            PoolInfo storage p = _poolInfo[level];
            if (price >= p.lowPrice && price < p.highPrice) {
                return level;
            }
        }
        // Fallback if misconfigured: Common
        return PoolLevel.Common;
    }
    // -----------------------
    // Selection (for PackManager)
    // -----------------------
    /// @notice Get count of NFTs at a pool level
    function getPoolLevelSize(PoolLevel level) external view returns (uint256) {
        return _poolInfo[level].totalNfts;
    }
    /// @notice Get NFT at global index within a pool level (0..size-1)
    /// @dev Walks collections and per-collection token arrays; O(number of collections in pool).
    function getPoolLevelNftAt(
        PoolLevel level,
        uint256 index
    ) external view returns (address collection, uint256 tokenId) {
        if (index >= _poolInfo[level].totalNfts) revert IndexOutOfBounds();
        return _selectNftAtIndex(level, index);
    }
    /// @notice Select and transfer a random NFT from a pool level
    /// @dev Uses pool.totalNfts and walks collections; no per-NFT migration on floor price changes.
    function selectAndTransferFromLevel(
        PoolLevel level,
        uint256 randomValue,
        address to
    ) external onlyRole(POOL_MANAGER_ROLE) returns (address collection, uint256 tokenId) {
        uint256 total = _poolInfo[level].totalNfts;
        if (total == 0) revert LevelEmpty(level);
        (collection, tokenId) = _selectNftAtIndex(level, randomValue % total);
        _removeCollectionToken(collection, tokenId, level);
        IERC721(collection).safeTransferFrom(address(this), to, tokenId);
        emit Withdrawn(to, collection, tokenId, level);
    }
    /// @notice Select and lock a random NFT from a pool level without transferring it out.
    /// @dev Used by PackManager so NFTs stay owned by NftPool and are just removed from accounting.
    function selectAndLockFromLevel(
        PoolLevel level,
        uint256 randomValue
    ) external onlyRole(POOL_MANAGER_ROLE) returns (address collection, uint256 tokenId) {
        uint256 total = _poolInfo[level].totalNfts;
        if (total == 0) revert LevelEmpty(level);
        (collection, tokenId) = _selectNftAtIndex(level, randomValue % total);
        // Remove from accounting so it cannot be selected again while locked in a pack
        _removeCollectionToken(collection, tokenId, level);
    }
    /// @dev Find NFT at global index within a pool level
    function _selectNftAtIndex(
        PoolLevel level,
        uint256 globalIndex
    ) internal view returns (address collection, uint256 tokenId) {
        address[] storage collections = _poolInfo[level].collections;
        uint256 remaining = globalIndex;
        for (uint256 i = 0; i < collections.length; i++) {
            address coll = collections[i];
            uint256 count = _collectionTokens[coll].length;
            if (count > 0 && remaining < count) {
                return (coll, _collectionTokens[coll][remaining]);
            }
            remaining -= count;
        }
        revert LevelEmpty(level);
    }
    // -----------------------
    // Locking helpers (for PackManager)
    // -----------------------
    /// @notice Re-add a previously locked NFT back into the pool accounting.
    /// @dev NFT must still be owned by the pool and not currently tracked.
    function addLockedNft(address collection, uint256 tokenId) external onlyRole(POOL_MANAGER_ROLE) {
        CollectionInfo storage info = _collectionInfo[collection];
        if (info.poolIndexPlusOne == 0) revert CollectionNotAllowed();
        if (IERC721(collection).ownerOf(tokenId) != address(this)) revert NotInPool();
        if (_collectionTokenIndexPlusOne[collection][tokenId] != 0) revert AlreadyTracked();
        _addCollectionToken(collection, tokenId, info.poolLevel);
    }
    /// @notice Transfer a locked NFT (no longer tracked in accounting) to a recipient.
    /// @dev Used by PackManager in the NFT-claim path. Does not change pool totals because
    /// the NFT was removed from accounting when it was locked.
    function transferLockedNft(address collection, address to, uint256 tokenId) external onlyRole(POOL_MANAGER_ROLE) {
        if (_collectionTokenIndexPlusOne[collection][tokenId] != 0) revert AlreadyTracked();
        if (IERC721(collection).ownerOf(tokenId) != address(this)) revert NotInPool();
        PoolLevel level = _collectionInfo[collection].poolLevel;
        IERC721(collection).safeTransferFrom(address(this), to, tokenId);
        emit Withdrawn(to, collection, tokenId, level);
    }
    // -----------------------
    // Views
    // -----------------------
    /// @notice Get total NFTs across all pools
    function totalPoolSize() external view returns (uint256 total) {
        for (uint256 i = 0; i < _POOL_LEVEL_COUNT; i++) {
            total += _poolInfo[PoolLevel(i)].totalNfts;
        }
    }
    /// @notice Get pool price range configuration
    function getPoolInfo(PoolLevel level) external view returns (uint256 lowPrice, uint256 highPrice) {
        PoolInfo storage info = _poolInfo[level];
        return (info.lowPrice, info.highPrice);
    }
    /// @notice Get collections currently assigned to a pool level
    function getPoolCollections(PoolLevel level) external view returns (address[] memory) {
        return _poolInfo[level].collections;
    }
    /// @notice Get collection info
    function getCollectionInfo(
        address collection
    ) external view returns (bool allowed, uint256 floorPrice, PoolLevel poolLevel) {
        CollectionInfo storage info = _collectionInfo[collection];
        allowed = info.poolIndexPlusOne != 0;
        floorPrice = info.floorPrice;
        poolLevel = info.poolLevel;
    }
    /// @notice Get floor price for a collection
    function getCollectionFloorPrice(address collection) external view returns (uint256) {
        return _collectionInfo[collection].floorPrice;
    }
    /// @notice Check if collection is allowed (i.e. assigned to some pool)
    function isCollectionAllowed(address collection) external view returns (bool) {
        return _collectionInfo[collection].poolIndexPlusOne != 0;
    }
    /// @notice Get pool level for a collection based on current assignment
    function getCollectionPoolLevel(address collection) external view returns (PoolLevel) {
        CollectionInfo storage info = _collectionInfo[collection];
        return info.poolLevel;
    }
    /// @notice Check if NFT is tracked in pool
    function isNftInPool(address collection, uint256 tokenId) external view returns (bool) {
        return _collectionTokenIndexPlusOne[collection][tokenId] != 0;
    }
    /// @notice Get NFT's current pool level
    function getNftPoolLevel(address collection, uint256 tokenId) external view returns (PoolLevel) {
        if (_collectionTokenIndexPlusOne[collection][tokenId] == 0) {
            revert NotInPool();
        }
        return _collectionInfo[collection].poolLevel;
    }
    // -----------------------
    // Overrides
    // -----------------------
    function supportsInterface(
        bytes4 interfaceId
    ) public view override(AccessControlUpgradeable) returns (bool) {
        return interfaceId == type(IERC721Receiver).interfaceId || super.supportsInterface(interfaceId);
    }
    // -----------------------
    // Storage Gap
    // -----------------------
    uint256[44] private __gap;
}

project/contracts/PackManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

// <ai_context>
// PackManager coordinates opening of RARI pack NFTs using Chainlink VRF. It
// selects NFTs from NftPool into specific packs, locks them as pack contents
// while keeping actual ownership inside NftPool, and then lets users either
// claim those NFTs or claim an instant-cash reward. In the instant-cash path,
// NFTs are simply re-added back into NftPool accounting so the pool can
// continue to exist and be reused for future packs. It is designed to work
// together with RariPack and NftPool.
// </ai_context>

import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import {ERC721HolderUpgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC721/utils/ERC721HolderUpgradeable.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";

import {RariPack} from "./RariPack.sol";
import {NftPool} from "./NftPool.sol";

/// @title PackManager
/// @notice Opens RariPack NFTs and distributes rewards from NftPool using Chainlink VRF
/// @dev Uses Chainlink VRF v2.5 for verifiable randomness when selecting NFTs
/// @dev Selection: first select pool level by probability, then select NFT from that level with equal probability
/// @dev Supports a 2-step flow: open pack (lock contents) and then claim either NFTs or instant cash
contract PackManager is Initializable, OwnableUpgradeable, ReentrancyGuardUpgradeable, PausableUpgradeable, ERC721HolderUpgradeable {
    // -----------------------
    // Types
    // -----------------------

    /// @dev Request state for pending VRF callbacks
    struct OpenRequest {
        address requester;
        uint256 packTokenId;
        RariPack.PackType packType;
        bool fulfilled;
    }

    /// @dev Reward NFT info for events
    struct RewardNft {
        address collection;
        uint256 tokenId;
        NftPool.PoolLevel poolLevel;
    }

    /// @dev Probability thresholds for each pack type (cumulative, out of 10000)
    struct PackProbabilities {
        uint16 ultraRare;
        uint16 legendary;
        uint16 epic;
        uint16 rare;
    }

    // -----------------------
    // Constants
    // -----------------------

    /// @dev Number of NFTs rewarded per pack opening
    uint256 public constant REWARDS_PER_PACK = 3;

    /// @dev Probability precision (10000 = 100.00%)
    uint256 public constant PROBABILITY_PRECISION = 10000;

    /// @dev Instant cash payout percentage (8000 = 80%)
    uint256 public constant INSTANT_CASH_PERCENTAGE = 8000;

    // -----------------------
    // Storage
    // -----------------------

    /// @dev RariPack contract reference
    RariPack public rariPack;

    /// @dev Single NftPool contract holding all NFTs
    NftPool public nftPool;

    /// @dev VRF Coordinator address
    address public vrfCoordinator;

    /// @dev VRF subscription ID
    uint256 public vrfSubscriptionId;

    /// @dev VRF key hash (gas lane)
    bytes32 public vrfKeyHash;

    /// @dev VRF callback gas limit
    uint32 public vrfCallbackGasLimit;

    /// @dev VRF request confirmations
    uint16 public vrfRequestConfirmations;

    /// @dev VRF request ID => OpenRequest
    mapping(uint256 => OpenRequest) public openRequests;

    /// @dev User address => array of pending request IDs
    mapping(address => uint256[]) private _userPendingRequests;

    /// @dev Pack type => probability thresholds
    mapping(RariPack.PackType => PackProbabilities) private _packProbabilities;

    /// @dev Treasury address for instant cash payouts (ETH)
    address public payoutTreasury;

    /// @dev Whether instant cash claims are enabled
    bool public instantCashEnabled;

    /// @dev Pack tokenId => whether a VRF open request is currently in progress
    mapping(uint256 => bool) public packOpeningInProgress;

    // -----------------------
    // Events
    // -----------------------

    event PackOpenRequested(
        uint256 indexed requestId,
        address indexed requester,
        uint256 indexed packTokenId,
        RariPack.PackType packType
    );

    event PackOpened(
        uint256 indexed requestId,
        address indexed requester,
        uint256 indexed packTokenId,
        RewardNft[3] rewards
    );

    event InstantCashClaimed(
        address indexed requester,
        uint256 indexed packTokenId,
        uint256 totalPayout,
        address[] collections,
        uint256[] tokenIds
    );

    event NftClaimed(
        address indexed requester,
        uint256 indexed packTokenId,
        address[] collections,
        uint256[] tokenIds
    );

    event NftPoolSet(address indexed poolAddress);
    event RariPackSet(address indexed rariPackAddress);
    event VrfConfigUpdated(
        address indexed coordinator,
        uint256 subscriptionId,
        bytes32 keyHash,
        uint32 callbackGasLimit,
        uint16 requestConfirmations
    );
    event PackProbabilitiesUpdated(
        RariPack.PackType indexed packType,
        uint16 ultraRare,
        uint16 legendary,
        uint16 epic,
        uint16 rare
    );
    event PayoutTreasuryUpdated(address indexed oldTreasury, address indexed newTreasury);
    event InstantCashEnabledUpdated(bool enabled);
    event TreasuryFunded(address indexed funder, uint256 amount);
    event TreasuryWithdrawn(address indexed to, uint256 amount);

    // -----------------------
    // Errors
    // -----------------------

    error ZeroAddress();
    error NotPackOwner();
    error PoolNotSet();
    error LevelEmpty(NftPool.PoolLevel level);
    error InvalidVrfCoordinator();
    error RequestNotFound();
    error RequestAlreadyFulfilled();
    error OnlyVrfCoordinator();
    error InvalidProbabilities();
    error InstantCashNotEnabled();
    error FloorPriceNotSet(address collection);
    error InsufficientTreasuryBalance();
    error PayoutTreasuryNotSet();
    error TransferFailed();
    error PackAlreadyOpened();
    error PackOpeningInProgressError();
    error PackNotOpened();
    error PackEmpty();

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // -----------------------
    // Initializer
    // -----------------------

    /// @notice Initialize the PackManager
    /// @param initialOwner Owner address
    /// @param rariPack_ RariPack contract address
    function initialize(address initialOwner, address rariPack_) external initializer {
        if (initialOwner == address(0)) revert ZeroAddress();
        if (rariPack_ == address(0)) revert ZeroAddress();

        __Ownable_init(initialOwner);
        __ReentrancyGuard_init();
        __Pausable_init();
        __ERC721Holder_init();

        rariPack = RariPack(rariPack_);
        emit RariPackSet(rariPack_);

        // Set default probabilities
        _setDefaultProbabilities();

        // Instant cash disabled by default
        instantCashEnabled = false;
    }

    /// @dev Set default probability thresholds (cumulative, out of 10000)
    /// Higher tier packs have better odds for rare items
    function _setDefaultProbabilities() internal {
        // Platinum (best odds + exclusive UltraRare access):
        // UltraRare 0.5%, Legendary 2%, Epic 7%, Rare 20%, Common 70.5%
        _packProbabilities[RariPack.PackType.Platinum] = PackProbabilities({
            ultraRare: 50, // 0.5%
            legendary: 250, // 2% (cumulative 2.5%)
            epic: 950, // 7% (cumulative 9.5%)
            rare: 2950 // 20% (cumulative 29.5%), Common = 70.5%
        });

        // Gold (great odds, no UltraRare):
        // Legendary 1%, Epic 5%, Rare 15%, Common 79%
        _packProbabilities[RariPack.PackType.Gold] = PackProbabilities({
            ultraRare: 0, // Not available
            legendary: 100, // 1%
            epic: 600, // 5% (cumulative 6%)
            rare: 2100 // 15% (cumulative 21%), Common = 79%
        });

        // Silver (good odds):
        // Legendary 0.5%, Epic 3%, Rare 10%, Common 86.5%
        _packProbabilities[RariPack.PackType.Silver] = PackProbabilities({
            ultraRare: 0, // Not available
            legendary: 50, // 0.5%
            epic: 350, // 3% (cumulative 3.5%)
            rare: 1350 // 10% (cumulative 13.5%), Common = 86.5%
        });

        // Bronze (entry level):
        // Legendary 0.2%, Epic 1%, Rare 5%, Common 93.8%
        _packProbabilities[RariPack.PackType.Bronze] = PackProbabilities({
            ultraRare: 0, // Not available
            legendary: 20, // 0.2%
            epic: 120, // 1% (cumulative 1.2%)
            rare: 620 // 5% (cumulative 6.2%), Common = 93.8%
        });
    }

    // -----------------------
    // Admin: Configuration
    // -----------------------

    /// @notice Set the RariPack contract
    function setRariPack(address rariPack_) external onlyOwner {
        if (rariPack_ == address(0)) revert ZeroAddress();
        rariPack = RariPack(rariPack_);
        emit RariPackSet(rariPack_);
    }

    /// @notice Set the NFT pool contract
    function setNftPool(address pool_) external onlyOwner {
        if (pool_ == address(0)) revert ZeroAddress();
        nftPool = NftPool(pool_);
        emit NftPoolSet(pool_);
    }

    /// @notice Configure Chainlink VRF parameters
    function setVrfConfig(
        address coordinator_,
        uint256 subscriptionId_,
        bytes32 keyHash_,
        uint32 callbackGasLimit_,
        uint16 requestConfirmations_
    ) external onlyOwner {
        if (coordinator_ == address(0)) revert ZeroAddress();
        vrfCoordinator = coordinator_;
        vrfSubscriptionId = subscriptionId_;
        vrfKeyHash = keyHash_;
        vrfCallbackGasLimit = callbackGasLimit_;
        vrfRequestConfirmations = requestConfirmations_;
        emit VrfConfigUpdated(coordinator_, subscriptionId_, keyHash_, callbackGasLimit_, requestConfirmations_);
    }

    /// @notice Set probability thresholds for a pack type
    function setPackProbabilities(
        RariPack.PackType packType,
        uint16 ultraRare,
        uint16 legendary,
        uint16 epic,
        uint16 rare
    ) external onlyOwner {
        if (ultraRare > legendary || legendary > epic || epic > rare || rare > PROBABILITY_PRECISION) {
            revert InvalidProbabilities();
        }

        _packProbabilities[packType] = PackProbabilities({
            ultraRare: ultraRare,
            legendary: legendary,
            epic: epic,
            rare: rare
        });

        emit PackProbabilitiesUpdated(packType, ultraRare, legendary, epic, rare);
    }

    /// @notice Batch set probabilities for all pack types
    function setAllPackProbabilities(
        PackProbabilities calldata platinumProbs,
        PackProbabilities calldata goldProbs,
        PackProbabilities calldata silverProbs,
        PackProbabilities calldata bronzeProbs
    ) external onlyOwner {
        _validateAndSetProbabilities(RariPack.PackType.Platinum, platinumProbs);
        _validateAndSetProbabilities(RariPack.PackType.Gold, goldProbs);
        _validateAndSetProbabilities(RariPack.PackType.Silver, silverProbs);
        _validateAndSetProbabilities(RariPack.PackType.Bronze, bronzeProbs);
    }

    function _validateAndSetProbabilities(RariPack.PackType packType, PackProbabilities calldata probs) internal {
        if (
            probs.ultraRare > probs.legendary ||
            probs.legendary > probs.epic ||
            probs.epic > probs.rare ||
            probs.rare > PROBABILITY_PRECISION
        ) {
            revert InvalidProbabilities();
        }

        _packProbabilities[packType] = probs;

        emit PackProbabilitiesUpdated(packType, probs.ultraRare, probs.legendary, probs.epic, probs.rare);
    }

    /// @notice Set the payout treasury address (for ETH management)
    function setPayoutTreasury(address treasury_) external onlyOwner {
        address oldTreasury = payoutTreasury;
        payoutTreasury = treasury_;
        emit PayoutTreasuryUpdated(oldTreasury, treasury_);
    }

    /// @notice Enable or disable instant cash claims
    function setInstantCashEnabled(bool enabled) external onlyOwner {
        instantCashEnabled = enabled;
        emit InstantCashEnabledUpdated(enabled);
    }

    /// @notice Pause pack opening and claiming
    function pause() external onlyOwner {
        _pause();
    }

    /// @notice Unpause pack opening and claiming
    function unpause() external onlyOwner {
        _unpause();
    }

    // -----------------------
    // Treasury Management
    // -----------------------

    /// @notice Fund the contract for instant cash payouts
    function fundTreasury() external payable {
        emit TreasuryFunded(msg.sender, msg.value);
    }

    /// @notice Withdraw funds from the contract
    function withdrawTreasury(address to, uint256 amount) external onlyOwner {
        if (to == address(0)) revert ZeroAddress();
        if (amount > address(this).balance) revert InsufficientTreasuryBalance();

        (bool sent, ) = to.call{value: amount}("");
        if (!sent) revert TransferFailed();

        emit TreasuryWithdrawn(to, amount);
    }

    /// @notice Get contract ETH balance for payouts
    function treasuryBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /// @notice Allow contract to receive ETH
    receive() external payable {
        emit TreasuryFunded(msg.sender, msg.value);
    }

    // -----------------------
    // Core: Open Pack
    // -----------------------

    /// @notice Request to open a pack and lock NFTs into it
    /// @dev After VRF fulfillment, NFTs are locked inside NftPool and contents are stored in RariPack.
    ///      The pack is not burned during opening.
    function openPack(uint256 packTokenId) external nonReentrant whenNotPaused returns (uint256 requestId) {
        // Verify caller owns the pack
        if (rariPack.ownerOf(packTokenId) != msg.sender) revert NotPackOwner();

        // Ensure pack not already opened
        (, , bool opened) = rariPack.getPackContents(packTokenId);
        if (opened) revert PackAlreadyOpened();

        // Ensure we are not already waiting for VRF for this pack
        if (packOpeningInProgress[packTokenId]) revert PackOpeningInProgressError();

        // Get pack type
        RariPack.PackType packType = rariPack.packTypeOf(packTokenId);

        // Verify pool is set and has NFTs at required levels
        _verifyPoolLevelsAvailable(packType);

        // Request randomness from VRF
        requestId = _requestRandomness();

        // Store request
        openRequests[requestId] = OpenRequest({
            requester: msg.sender,
            packTokenId: packTokenId,
            packType: packType,
            fulfilled: false
        });

        packOpeningInProgress[packTokenId] = true;

        _userPendingRequests[msg.sender].push(requestId);

        emit PackOpenRequested(requestId, msg.sender, packTokenId, packType);
    }

    /// @notice Callback function for VRF Coordinator
    function rawFulfillRandomWords(uint256 requestId, uint256[] calldata randomWords) external {
        if (msg.sender != vrfCoordinator) revert OnlyVrfCoordinator();
        _fulfillRandomWords(requestId, randomWords);
    }

    // -----------------------
    // Internal: VRF
    // -----------------------

    function _requestRandomness() internal returns (uint256 requestId) {
        if (vrfCoordinator == address(0)) revert InvalidVrfCoordinator();

        bytes memory data = abi.encodeWithSignature(
            "requestRandomWords(bytes32,uint256,uint16,uint32,uint32)",
            vrfKeyHash,
            vrfSubscriptionId,
            vrfRequestConfirmations,
            vrfCallbackGasLimit,
            uint32(REWARDS_PER_PACK)
        );

        (bool success, bytes memory returnData) = vrfCoordinator.call(data);
        require(success, "PackManager: VRF request failed");
        requestId = abi.decode(returnData, (uint256));
    }

    function _fulfillRandomWords(uint256 requestId, uint256[] calldata randomWords) internal {
        OpenRequest storage request = openRequests[requestId];
        if (request.requester == address(0)) revert RequestNotFound();
        if (request.fulfilled) revert RequestAlreadyFulfilled();

        request.fulfilled = true;
        packOpeningInProgress[request.packTokenId] = false;

        _processOpen(requestId, request, randomWords);

        _removePendingRequest(request.requester, requestId);
    }

    /// @dev Process opening of a pack - select and lock NFTs into the pack
    function _processOpen(
        uint256 requestId,
        OpenRequest storage request,
        uint256[] calldata randomWords
    ) internal {
        RewardNft[3] memory rewards;
        address[] memory collections = new address[](REWARDS_PER_PACK);
        uint256[] memory tokenIds = new uint256[](REWARDS_PER_PACK);

        for (uint256 i = 0; i < REWARDS_PER_PACK; i++) {
            uint256 randomValue = randomWords[i];

            // Determine pool level based on probability
            NftPool.PoolLevel level = _selectPoolLevel(request.packType, randomValue);

            // Select and lock random NFT from that level in the pool (no transfer out)
            (address collection, uint256 tokenId) = nftPool.selectAndLockFromLevel(
                level,
                randomValue >> 16 // Use different bits for NFT selection
            );

            rewards[i] = RewardNft({collection: collection, tokenId: tokenId, poolLevel: level});
            collections[i] = collection;
            tokenIds[i] = tokenId;
        }

        // Lock contents into the pack (for metadata and later claims)
        rariPack.setPackContents(request.packTokenId, collections, tokenIds);

        emit PackOpened(requestId, request.requester, request.packTokenId, rewards);
    }

    // -----------------------
    // Claims
    // -----------------------

    /// @notice Claim the NFTs locked in an opened pack and burn the pack.
    function claimNft(uint256 packTokenId) external nonReentrant whenNotPaused {
        if (rariPack.ownerOf(packTokenId) != msg.sender) revert NotPackOwner();

        (address[] memory collections, uint256[] memory tokenIds, bool opened) = rariPack.getPackContents(packTokenId);
        if (!opened) revert PackNotOpened();
        if (collections.length == 0) revert PackEmpty();

        for (uint256 i = 0; i < collections.length; i++) {
            nftPool.transferLockedNft(collections[i], msg.sender, tokenIds[i]);
        }

        rariPack.burnPack(packTokenId);

        emit NftClaimed(msg.sender, packTokenId, collections, tokenIds);
    }

    /// @notice Claim instant cash reward for an opened pack and burn the pack.
    /// @dev Locked NFTs remain owned by NftPool and are re-added to pool accounting
    ///      so the pool can continue to serve future packs, while the caller receives ETH.
    function claimReward(uint256 packTokenId) external nonReentrant whenNotPaused {
        if (!instantCashEnabled) revert InstantCashNotEnabled();

        if (rariPack.ownerOf(packTokenId) != msg.sender) revert NotPackOwner();

        (address[] memory collections, uint256[] memory tokenIds, bool opened) = rariPack.getPackContents(packTokenId);
        if (!opened) revert PackNotOpened();
        if (collections.length == 0) revert PackEmpty();

        uint256 totalPayout = 0;

        // Calculate payout based on collection floor prices
        for (uint256 i = 0; i < collections.length; i++) {
            uint256 floorPrice = nftPool.getCollectionFloorPrice(collections[i]);
            if (floorPrice == 0) revert FloorPriceNotSet(collections[i]);

            uint256 payout = (floorPrice * INSTANT_CASH_PERCENTAGE) / PROBABILITY_PRECISION;
            totalPayout += payout;
        }

        if (address(this).balance < totalPayout) revert InsufficientTreasuryBalance();

        // Re-add NFTs back into the pool accounting (they never left the pool contract)
        for (uint256 i = 0; i < collections.length; i++) {
            nftPool.addLockedNft(collections[i], tokenIds[i]);
        }

        // Burn pack
        rariPack.burnPack(packTokenId);

        // Send ETH payout to claimer
        (bool sent, ) = msg.sender.call{value: totalPayout}("");
        if (!sent) revert TransferFailed();

        emit InstantCashClaimed(msg.sender, packTokenId, totalPayout, collections, tokenIds);
    }

    // -----------------------
    // Internal: Pool Level Selection
    // -----------------------

    /// @dev Select pool level based on pack type and random value
    function _selectPoolLevel(
        RariPack.PackType packType,
        uint256 randomValue
    ) internal view returns (NftPool.PoolLevel) {
        uint256 roll = randomValue % PROBABILITY_PRECISION;
        PackProbabilities storage probs = _packProbabilities[packType];

        // For Platinum, check UltraRare first
        if (packType == RariPack.PackType.Platinum && roll < probs.ultraRare) {
            return NftPool.PoolLevel.UltraRare;
        }

        if (roll < probs.legendary) return NftPool.PoolLevel.Legendary;
        if (roll < probs.epic) return NftPool.PoolLevel.Epic;
        if (roll < probs.rare) return NftPool.PoolLevel.Rare;
        return NftPool.PoolLevel.Common;
    }

    /// @dev Verify that pool levels needed for pack type have NFTs
    function _verifyPoolLevelsAvailable(RariPack.PackType packType) internal view {
        if (address(nftPool) == address(0)) revert PoolNotSet();

        // Check required levels have NFTs
        if (nftPool.getPoolLevelSize(NftPool.PoolLevel.Common) == 0) revert LevelEmpty(NftPool.PoolLevel.Common);
        if (nftPool.getPoolLevelSize(NftPool.PoolLevel.Rare) == 0) revert LevelEmpty(NftPool.PoolLevel.Rare);
        if (nftPool.getPoolLevelSize(NftPool.PoolLevel.Epic) == 0) revert LevelEmpty(NftPool.PoolLevel.Epic);
        if (nftPool.getPoolLevelSize(NftPool.PoolLevel.Legendary) == 0) revert LevelEmpty(NftPool.PoolLevel.Legendary);

        PackProbabilities storage probs = _packProbabilities[packType];
        if (packType == RariPack.PackType.Platinum && probs.ultraRare > 0) {
            if (nftPool.getPoolLevelSize(NftPool.PoolLevel.UltraRare) == 0) {
                revert LevelEmpty(NftPool.PoolLevel.UltraRare);
            }
        }
    }

    function _removePendingRequest(address user, uint256 requestId) internal {
        uint256[] storage pending = _userPendingRequests[user];
        for (uint256 i = 0; i < pending.length; i++) {
            if (pending[i] == requestId) {
                pending[i] = pending[pending.length - 1];
                pending.pop();
                break;
            }
        }
    }

    // -----------------------
    // Views
    // -----------------------

    /// @notice Get all pending request IDs for a user
    function getPendingRequests(address user) external view returns (uint256[] memory) {
        return _userPendingRequests[user];
    }

    /// @notice Get probability thresholds for a pack type
    function getPackProbabilities(
        RariPack.PackType packType
    ) external view returns (uint16 ultraRare, uint16 legendary, uint16 epic, uint16 rare) {
        PackProbabilities storage probs = _packProbabilities[packType];
        return (probs.ultraRare, probs.legendary, probs.epic, probs.rare);
    }

    /// @notice Get individual pool probabilities as percentages (in basis points)
    function getPackProbabilitiesPercent(
        RariPack.PackType packType
    )
        external
        view
        returns (
            uint16 ultraRarePercent,
            uint16 legendaryPercent,
            uint16 epicPercent,
            uint16 rarePercent,
            uint16 commonPercent
        )
    {
        PackProbabilities storage probs = _packProbabilities[packType];

        ultraRarePercent = probs.ultraRare;
        legendaryPercent = probs.legendary - probs.ultraRare;
        epicPercent = probs.epic - probs.legendary;
        rarePercent = probs.rare - probs.epic;
        commonPercent = uint16(PROBABILITY_PRECISION) - probs.rare;
    }

    /// @notice Check if pack can be opened (pool configured and levels have NFTs)
    function canOpenPack(RariPack.PackType packType) external view returns (bool) {
        if (address(nftPool) == address(0)) return false;

        if (nftPool.getPoolLevelSize(NftPool.PoolLevel.Common) == 0) return false;
        if (nftPool.getPoolLevelSize(NftPool.PoolLevel.Rare) == 0) return false;
        if (nftPool.getPoolLevelSize(NftPool.PoolLevel.Epic) == 0) return false;
        if (nftPool.getPoolLevelSize(NftPool.PoolLevel.Legendary) == 0) return false;

        PackProbabilities storage probs = _packProbabilities[packType];
        if (packType == RariPack.PackType.Platinum && probs.ultraRare > 0) {
            if (nftPool.getPoolLevelSize(NftPool.PoolLevel.UltraRare) == 0) return false;
        }

        return true;
    }

    /// @notice Get the NFT pool address
    function getNftPool() external view returns (address) {
        return address(nftPool);
    }

    /// @notice Calculate potential instant cash payout for a collection
    function getInstantCashPayout(address collection) external view returns (uint256 payout) {
        if (address(nftPool) == address(0)) return 0;
        uint256 floorPrice = nftPool.getCollectionFloorPrice(collection);
        if (floorPrice == 0) return 0;
        payout = (floorPrice * INSTANT_CASH_PERCENTAGE) / PROBABILITY_PRECISION;
    }

    /// @notice Get the instant cash percentage (80%)
    function getInstantCashPercentage() external pure returns (uint256) {
        return INSTANT_CASH_PERCENTAGE;
    }

    // -----------------------
    // Storage Gap
    // -----------------------

    uint256[35] private __gap;
}

project/contracts/RariPack.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

// <ai_context>
// RariPack is the ERC721 contract that mints RARI pack NFTs. It tracks pack
// types, pricing and treasury, and exposes fully on-chain, dynamic metadata
// including pack open state and the list of locked NFT contents. The metadata
// is consumed by off-chain indexers and UIs together with PackManager and
// NftPool.
// </ai_context>

import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {ERC721Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {AccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {Base64} from "@openzeppelin/contracts/utils/Base64.sol";

contract RariPack is
    Initializable,
    ERC721Upgradeable,
    OwnableUpgradeable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable
{
    using Strings for uint256;

    // -----------------------
    // Types
    // -----------------------

    enum PackType {
        Bronze,
        Silver,
        Gold,
        Platinum
    }

    // -----------------------
    // Roles
    // -----------------------

    /// @dev Contracts that are allowed to burn packs and manage contents
    ///      (e.g. PackManager).
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");
    // DEFAULT_ADMIN_ROLE comes from AccessControl (0x00)

    // -----------------------
    // Storage
    // -----------------------

    /// @dev Incremental token id counter
    uint256 private _nextTokenId;

    /// @dev tokenId => pack type
    mapping(uint256 => PackType) private _tokenPackType;

    /// @dev Price per pack type in wei
    mapping(PackType => uint256) private _packPrices;

    /// @dev Per-pack-type image or external URI used in dynamic metadata
    mapping(PackType => string) private _packURIs;

    /// @dev Address that receives mint proceeds
    address public treasury;

    /// @dev Whether a pack has been opened (its contents locked and revealed)
    mapping(uint256 => bool) private _packOpened;

    /// @dev Per-pack locked NFT contents (collections)
    mapping(uint256 => address[]) private _packNftCollections;

    /// @dev Per-pack locked NFT contents (tokenIds)
    mapping(uint256 => uint256[]) private _packNftTokenIds;

    /// @dev Optional human-readable description per pack type
    mapping(PackType => string) private _packDescriptions;

    // -----------------------
    // Events / Errors
    // -----------------------

    event PackPriceUpdated(PackType indexed packType, uint256 oldPrice, uint256 newPrice);
    event TreasuryUpdated(address indexed oldTreasury, address indexed newTreasury);
    event PackURIUpdated(PackType indexed packType, string oldURI, string newURI);
    event PackDescriptionUpdated(PackType indexed packType, string oldDescription, string newDescription);
    event PackContentsUpdated(uint256 indexed tokenId, address[] collections, uint256[] tokenIds);

    error TreasuryNotSet();
    error IncorrectEthSent();
    error ZeroAmount();
    error PriceNotSet();
    error ArrayLengthMismatch();

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // -----------------------
    // Init
    // -----------------------

    function initialize(
        address initialOwner,
        address initialTreasury,
        string memory name_,
        string memory symbol_
    ) external initializer {
        __ERC721_init(name_, symbol_);
        __Ownable_init(initialOwner);
        __AccessControl_init();
        __ReentrancyGuard_init();

        // AccessControl admin & burner
        _grantRole(DEFAULT_ADMIN_ROLE, initialOwner);
        _grantRole(BURNER_ROLE, initialOwner);

        // Set treasury
        treasury = initialTreasury;
        emit TreasuryUpdated(address(0), initialTreasury);

        _nextTokenId = 1;
    }

    // -----------------------
    // Mint / Burn
    // -----------------------

    /// @notice Public mint: anyone can buy packs by paying the price in ETH.
    /// @param to Address that will receive the packs.
    /// @param packType Type of pack to mint.
    /// @param amount Number of packs (NFTs) to mint.
    function mintPack(address to, PackType packType, uint256 amount) external payable nonReentrant {
        if (amount == 0) revert ZeroAmount();

        uint256 pricePerPack = _packPrices[packType];
        if (pricePerPack == 0) revert PriceNotSet();

        uint256 totalPrice = pricePerPack * amount;
        if (msg.value != totalPrice) revert IncorrectEthSent();

        if (treasury == address(0)) revert TreasuryNotSet();

        for (uint256 i = 0; i < amount; i++) {
            uint256 tokenId = _nextTokenId++;
            _tokenPackType[tokenId] = packType;
            _safeMint(to, tokenId);
        }

        // Forward ETH to treasury
        (bool sent, ) = treasury.call{value: totalPrice}("");
        require(sent, "RariPack: ETH transfer failed");
    }

    /// @notice Burn a pack token and clear its metadata.
    /// @dev Only BURNER_ROLE (e.g. PackManager) can call this.
    function burnPack(uint256 tokenId) external onlyRole(BURNER_ROLE) {
        _burn(tokenId);
        delete _tokenPackType[tokenId];
        delete _packOpened[tokenId];
        delete _packNftCollections[tokenId];
        delete _packNftTokenIds[tokenId];
    }

    // -----------------------
    // Pricing
    // -----------------------

    /// @notice Set price for a given pack type (in wei).
    /// Only DEFAULT_ADMIN_ROLE can call.
    function setPackPrice(PackType packType, uint256 newPrice) external onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 oldPrice = _packPrices[packType];
        _packPrices[packType] = newPrice;
        emit PackPriceUpdated(packType, oldPrice, newPrice);
    }

    /// @notice Get price for a given pack type (in wei).
    function packPrice(PackType packType) external view returns (uint256) {
        return _packPrices[packType];
    }

    /// @notice Convenience getter by packTypeId (0..3: Bronze..Platinum).
    function packPriceById(uint256 packTypeId) external view returns (uint256) {
        require(packTypeId <= uint256(type(PackType).max), "RariPack: invalid pack type id");
        return _packPrices[PackType(packTypeId)];
    }

    // -----------------------
    // Metadata per pack type
    // -----------------------

    /// @notice Set image/external URI for a given pack type.
    /// Only DEFAULT_ADMIN_ROLE can call.
    function setPackURI(PackType packType, string calldata newURI) external onlyRole(DEFAULT_ADMIN_ROLE) {
        string memory oldURI = _packURIs[packType];
        _packURIs[packType] = newURI;
        emit PackURIUpdated(packType, oldURI, newURI);
    }

    /// @notice Get image/external URI for a pack type.
    function packURI(PackType packType) external view returns (string memory) {
        return _packURIs[packType];
    }

    /// @notice Set human-readable description for a pack type.
    function setPackDescription(PackType packType, string calldata newDescription) external onlyRole(DEFAULT_ADMIN_ROLE) {
        string memory oldDescription = _packDescriptions[packType];
        _packDescriptions[packType] = newDescription;
        emit PackDescriptionUpdated(packType, oldDescription, newDescription);
    }

    /// @notice Get human-readable description for a pack type.
    function packDescription(PackType packType) external view returns (string memory) {
        return _packDescriptions[packType];
    }

    /// @notice ERC721 tokenURI  returns dynamically generated JSON based on pack state.
    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        _requireOwned(tokenId);
        PackType packType = _tokenPackType[tokenId];

        string memory packTypeStr = _packTypeToString(packType);
        string memory idStr = tokenId.toString();
        string memory name = string(abi.encodePacked("RARI ", packTypeStr, " Pack #", idStr));

        string memory description = _packDescriptions[packType];
        if (bytes(description).length == 0) {
            description = string(
                abi.encodePacked(
                    "RARI ",
                    packTypeStr,
                    " Pack containing randomly selected NFTs across rarity pools."
                )
            );
        }

        bool opened = _packOpened[tokenId];

        address[] storage collectionsStorage = _packNftCollections[tokenId];
        uint256[] storage tokenIdsStorage = _packNftTokenIds[tokenId];
        uint256 contentsLen = collectionsStorage.length;

        string memory attrs = _buildAttributes(packTypeStr, opened, contentsLen);
        string memory nftsJson;
        if (opened && contentsLen > 0) {
            nftsJson = _buildNftsJson(collectionsStorage, tokenIdsStorage);
        }

        string memory image = _packURIs[packType];

        bytes memory json = abi.encodePacked(
            '{"name":"',
            name,
            '","description":"',
            description,
            '","attributes":',
            attrs
        );

        if (bytes(image).length != 0) {
            json = abi.encodePacked(json, ',"image":"', image, '"');
        }

        if (opened && contentsLen > 0) {
            json = abi.encodePacked(json, ',"nfts":', nftsJson);
        }

        json = abi.encodePacked(json, "}");

        string memory encoded = Base64.encode(json);
        return string(abi.encodePacked("data:application/json;base64,", encoded));
    }

    function _packTypeToString(PackType packType) internal pure returns (string memory) {
        if (packType == PackType.Bronze) return "Bronze";
        if (packType == PackType.Silver) return "Silver";
        if (packType == PackType.Gold) return "Gold";
        return "Platinum";
    }

    function _buildAttributes(
        string memory packTypeStr,
        bool opened,
        uint256 contentsLen
    ) internal pure returns (string memory) {
        string memory state = opened ? "Opened" : "Unopened";

        return
            string(
                abi.encodePacked(
                    '[{"trait_type":"Pack Type","value":"',
                    packTypeStr,
                    '"},{"trait_type":"Opened","value":"',
                    opened ? "true" : "false",
                    '"},{"trait_type":"State","value":"',
                    state,
                    '"},{"trait_type":"Contents Locked","value":"',
                    contentsLen.toString(),
                    '"}]'
                )
            );
    }

    function _buildNftsJson(
        address[] storage collections,
        uint256[] storage tokenIds
    ) internal view returns (string memory) {
        uint256 len = collections.length;
        bytes memory out = "[";

        for (uint256 i = 0; i < len; i++) {
            if (i > 0) {
                out = abi.encodePacked(out, ",");
            }
            out = abi.encodePacked(
                out,
                '{"collection":"',
                Strings.toHexString(uint160(collections[i]), 20),
                '","tokenId":"',
                tokenIds[i].toString(),
                '"}'
            );
        }

        out = abi.encodePacked(out, "]");
        return string(out);
    }

    // -----------------------
    // Pack contents (for PackManager)
    // -----------------------

    /// @notice Set the locked NFT contents for a pack.
    /// @dev Expected to be called by PackManager after randomness is fulfilled.
    function setPackContents(
        uint256 tokenId,
        address[] calldata collections,
        uint256[] calldata tokenIds
    ) external onlyRole(BURNER_ROLE) {
        _requireOwned(tokenId);
        if (collections.length != tokenIds.length) revert ArrayLengthMismatch();

        delete _packNftCollections[tokenId];
        delete _packNftTokenIds[tokenId];

        for (uint256 i = 0; i < collections.length; i++) {
            _packNftCollections[tokenId].push(collections[i]);
            _packNftTokenIds[tokenId].push(tokenIds[i]);
        }

        _packOpened[tokenId] = true;

        emit PackContentsUpdated(tokenId, collections, tokenIds);
    }

    /// @notice Get the locked NFT contents for a pack.
    function getPackContents(
        uint256 tokenId
    ) external view returns (address[] memory collections, uint256[] memory tokenIds, bool opened) {
        _requireOwned(tokenId);

        opened = _packOpened[tokenId];

        address[] storage storedCollections = _packNftCollections[tokenId];
        uint256[] storage storedTokenIds = _packNftTokenIds[tokenId];
        uint256 len = storedCollections.length;

        collections = new address[](len);
        tokenIds = new uint256[](len);

        for (uint256 i = 0; i < len; i++) {
            collections[i] = storedCollections[i];
            tokenIds[i] = storedTokenIds[i];
        }
    }

    // -----------------------
    // Treasury
    // -----------------------

    /// @notice Change treasury address. Only DEFAULT_ADMIN_ROLE can call.
    function setTreasury(address newTreasury) external onlyRole(DEFAULT_ADMIN_ROLE) {
        emit TreasuryUpdated(treasury, newTreasury);
        treasury = newTreasury;
    }

    // -----------------------
    // Helpers / Overrides
    // -----------------------

    /// @notice Get pack type of a given tokenId.
    function packTypeOf(uint256 tokenId) external view returns (PackType) {
        _requireOwned(tokenId);
        return _tokenPackType[tokenId];
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view override(ERC721Upgradeable, AccessControlUpgradeable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    uint256[50] private __gap;
}
